import React ,{useEffect,useState}from 'react';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import ListSubheader from '@material-ui/core/ListSubheader';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Axios from"axios";
import ScriptTag from 'react-script-tag';
import { compose, withProps, withHandlers, withState } from "recompose"
import { withScriptjs, withGoogleMap, GoogleMap, Marker } from "react-google-maps"


const MyMapComponent = compose(
    withProps({
        googleMapURL: "https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places",
        loadingElement: <div style={{ height: `100%` }} />,
        containerElement: <div style={{ height: `400px` }} />,
        mapElement: <div style={{ height: `100%` }} />,
    }),
    withScriptjs,
    withGoogleMap,
    withState('places', 'updatePlaces', ''),
    withHandlers(() => {
        const refs = {
            map: undefined,
        }

        return {
            onMapMounted: () => ref => {
                refs.map = ref
            },
            fetchPlaces: ({ updatePlaces }) => {
                let places;
                const bounds = refs.map.getBounds();
                const service = new google.maps.places.PlacesService(refs.map.context.__SECRET_MAP_DO_NOT_USE_OR_YOU_WILL_BE_FIRED);
                const request = {
                    bounds: bounds,
                    type: ['hotel']
                };
                service.nearbySearch(request, (results, status) => {
                    if (status == google.maps.places.PlacesServiceStatus.OK) {
                        console.log(results);
                        updatePlaces(results);
                    }
                })
            }
        }
    }),
)((props) => {
    return (
        <GoogleMap
            onTilesLoaded={props.fetchPlaces}
            ref={props.onMapMounted}
            onBoundsChanged={props.fetchPlaces}
            defaultZoom={8}
            defaultCenter={{ lat: 51.508530, lng: -0.076132 }}
        >
            {props.places && props.places.map((place, i) =>
                <Marker key={i} position={{ lat: place.geometry.location.lat(), lng: place.geometry.location.lng() }} />
            )}
        </GoogleMap>
    )
})

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    display:"flex",
    flexDirection:"row"

  },
}));


export default  function GroupedSelect() {

    const [city,setCity]= useState([]);
    const [prefucture,setPrefucture]=useState([]);
    const [cityForSearch,setCityForSearch]=useState("");
    const [saunaList,setSaunaList]=useState(["a"]);

    useEffect(() => {
    　Axios.get("https://opendata.resas-portal.go.jp/api/v1/prefectures",{
            headers:{"X-API-KEY":"JZ6Nzmm6F54YU3FD7RvDEZ1qUU9RgpbGF1GdkObS"}}).then((res)=>{
                const result=res.data.result
                result.map((val)=>{
                   setPrefucture(prev=>[...prev,val])})
         return(<ScriptTag isHydrating={true} type="text/javascript" src="https://maps.googleapis.com/maps/api/js?key=AIzaSyD7UflDjHf3NfCuXyGKBNO6iQMcgz_T594&libraries=places" />);
                })            
        },[]);

  const classes = useStyles();

  const getCity=(code)=>{
    Axios.get(`https://opendata.resas-portal.go.jp/api/v1/cities?prefCode=${code}`,{
        headers:{"X-API-KEY":"JZ6Nzmm6F54YU3FD7RvDEZ1qUU9RgpbGF1GdkObS"}}).then((res)=>{
            const result=res.data.result
            setCity([]);
            result.map((val)=>{
                setCity(prev=>[...prev,val])})
        })
                     };

    const getSaunaList=(city)=>{
    Axios.get(`https://maps.googleapis.com/maps/api/place/textsearch/json?query=サウナ+${city}&key=AIzaSyD7UflDjHf3NfCuXyGKBNO6iQMcgz_T594`).then((res)=>{
            const result=res.result
            setSaunaList([]);
            result.map((val)=>{
                setCity(prev=>[...prev,val])})
        })
                        };

  return (


    <div>

      <FormControl className={classes.formControl}>
        <InputLabel htmlFor="grouped-native-select1">都道府県</InputLabel>
        <Select native defaultValue="" id="grouped-native-select1" onChange={(e)=>{getCity(e.target.value)}}>
          <option aria-label="None" value="" />
          <optgroup label="都道府県">
                {prefucture.map((val)=>{
                    return(
                    <option value={val.prefCode}>{val.prefName}</option>)
                })}
          </optgroup>
          </Select>
          <InputLabel htmlFor="grouped-native-select2"></InputLabel>
          <Select native defaultValue="" id="grouped-native-select2" onChange={(e)=>{
              setCityForSearch(e.target.value)
              }}>
          <option aria-label="None"/>
          <optgroup label="市区町村">
                {city.map((val)=>{
                    return(
                    <option >{val.cityName}</option>)
                })}
          </optgroup>
          </Select>
          <InputLabel htmlFor="grouped-native-select3"></InputLabel>
          <Select native defaultValue="" id="grouped-native-select3" onChange={()=>{getSaunaList(cityForSearch)}}>
          <option aria-label="None"/>
          <optgroup label= "サウナ">
                {saunaList.map((val)=>{
                    return(
                    <option >{val.name}</option>)
                })}
          </optgroup>
          </Select>
      </FormControl>
    </div>
  );
    }
